function login(){$.ajax({type:"POST",url:"http://localhost:3000/signin",data:{user:{email:userEmail,password:userPassword}},success:function(o){console.log("response => ",o),authToken=o.auth_token,getContent()},error:function(o,e,s){console.log(o.status),console.log(s)}})}function getContent(){$.ajax({type:"GET",url:"http://localhost:3000/secret",beforeSend:function(o){o.setRequestHeader("User-Email",userEmail),o.setRequestHeader("Auth-Token",authToken)},success:function(o){console.log("response => ",o)},error:function(o,e,s){console.log(o.status),console.log(s)}})}var userEmail="j@random.com",userPassword="password",authToken;$(function(){login()});