function login(o){$.ajax({type:"POST",url:serverURL+"login",data:o,success:function(o){window.localStorage.email=$("#email").val(),window.localStorage.auth_token=o.auth_token,window.location.href="dashboard.html"},error:function(o,e,n){console.log(o.status),console.log(n),window.alert("Login Failed")}})}var serverURL="https://project3pockety.herokuapp.com/";$(function(){$("#login-form").on("submit",function(o){o.preventDefault();var e=$(this).serialize();login(e)})});